import React from 'react';
import { Stage, Layer, Rect } from 'react-konva';
import { Text } from 'react-konva';


const ParkingMap = ({ spots, onSpotClick }) => {
  return (
    <div>
      <h2>Peta Parkiran</h2>
      <Stage width={window.innerWidth} height={300}>
        <Layer>
          {spots.map((spot, index) => (
            <Rect
              key={spot.id}
              x={index * 100}
              y={50}
              width={80}
              height={80}
              fill={spot.status === 'available' ? 'green' : 'red'}
              onClick={() => onSpotClick(spot.id)}
            />
          ))}
        </Layer>
      </Stage>
    </div>
  );
};

{spots.map((spot, index) => (
    <>
      <Rect
        key={spot.id}
        x={index * 100}
        y={50}
        width={80}
        height={80}
        fill={spot.status === 'available' ? 'green' : 'red'}
        onClick={() => onSpotClick(spot.id)}
      />
      <Text
        x={index * 100 + 25}
        y={100}
        text={`P${spot.id}`}
        fontSize={18}
        fill="white"
      />
    </>
  ))}

export default ParkingMap;
