import React from 'react';
import { Stage, Layer, Rect, Text } from 'react-konva';

const ParkingMap = ({ spots, onSpotClick }) => {
  const gridSize = 4; // Jumlah kolom dalam grid
  const boxSize = 80; // Ukuran kotak tempat parkir
  const gap = 20; // Jarak antar kotak

  return (
    <div>
      <h2 className="text-center mb-4">Peta Parkiran</h2>
      <Stage width={window.innerWidth} height={300}>
        <Layer>
          {spots.map((spot, index) => {
            const row = Math.floor(index / gridSize); // Tentukan baris
            const col = index % gridSize; // Tentukan kolom

            return (
              <React.Fragment key={spot.id}>
                {/* Tempat parkir */}
                <Rect
                  x={col * (boxSize + gap)}
                  y={row * (boxSize + gap)}
                  width={boxSize}
                  height={boxSize}
                  fill={spot.status === 'available' ? 'green' : 'red'}
                  cornerRadius={10} // Membulatkan sudut
                  shadowBlur={5}
                  onClick={() => onSpotClick(spot.id)}
                />

                {/* Label untuk tempat parkir */}
                <Text
                  x={col * (boxSize + gap) + 20}
                  y={row * (boxSize + gap) + 25}
                  text={`P${spot.id}`}
                  fontSize={16}
                  fill="white"
                  align="center"
                />
              </React.Fragment>
            );
          })}
        </Layer>
      </Stage>
    </div>
  );
};

export default ParkingMap;
